import image from '../image.webp';
import'../Style/card.css';
const Card = () => {
  // Generate random image URL
  const getRandomImage = () => {
    // Replace this logic with your own random image URL generation
    const randomImageUrls = [image, image, image];
    const randomIndex = Math.floor(Math.random() * randomImageUrls.length);
    return randomImageUrls[randomIndex];
  };

  // Array of card data
  const cardData = [
    { name: 'One Plus', message: 'Android Device' },
    { name: 'Samsung S22', message: 'Android Device' },
    { name: 'Iphone 14', message: 'IOS Device' },
  ];

  return (
    <>
      {cardData.map((item, index) => (
        <div class="container" ontouchstart="this.classList.toggle('hover');">
  <div class="card">  
    <div class="card_front">
      <h1 class="card-symbol">&#128628;</h1>
    </div>

    <div class="card_back">
      <h1 class="card-symbol">&#128628;</h1>
      <div class="card-text">
        <p>The word <strong>ampersand</strong> is a corruption of the phrase "and per se & (and)", meaning "and intrinsically the word and (represented by the symbol &)".</p>
      <p>The ampersand can be traced back to the 1st century A.D. and the Old Roman cursive, in which the letters E and T occasionally were written together to form a ligature.</p>
      </div>
    </div>    

  </div>
</div>
  
      ))}
  </>
  );
};

export default Card;
