import image from '../image.webp';
import'../Style/card.css';
const Card = () => {
  // Generate random image URL
  const getRandomImage = () => {
    // Replace this logic with your own random image URL generation
    const randomImageUrls = [image, image, image];
    const randomIndex = Math.floor(Math.random() * randomImageUrls.length);
    return randomImageUrls[randomIndex];
  };

  // Array of card data
  const cardData = [
    { name: 'One Plus', message: 'Android Device' },
    { name: 'Samsung S22', message: 'Android Device' },
    { name: 'Iphone 14', message: 'IOS Device' },
  ];

  return (
        <div className="Main">
      {cardData.map((item, index) => (
     <div class="flip-card">
     <div class="flip-card-inner">
       <div class="flip-card-front">
         <img src="img_avatar.png" alt="Avatar" style={{width:"300px" ,height:"300px;"}}/>
       </div>
       <div class="flip-card-back">
         <h1>John Doe</h1> 
         <p>Architect & Engineer</p> 
         <p>We love that guy</p>
       </div>
     </div>
   </div>
  
      ))}
</div>
  );
};

export default Card;
