import image from '../image.webp';
import useNavigate from 'react-dom';
import'../Style/App.css';
const Card = () => {
  const navigate=useNavigate();
  // Generate random image URL



  // Array of card data
  const cardData = [
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },
    { name: 'One Plus', message: "More Details >", img: image },

    { name: 'Samsung S22', message: "More Details >", img: image },
    { name: 'Iphone 14', message: 'More Details >', img: image },
  ]; 

  return (
    <>
    {cardData.map((item, index) => (
      <figure class="snip1577" key={index}>
        <img src={item.img} alt="sample99" />
        <figcaption>
          <h3 onClick={() =>
                        navigate("/discription")}>{item.name}</h3>
          <h4><a href=''>{item.message}</a></h4>
         
      
        </figcaption>
      
      </figure>
      
    ))}
    </>
  );
};

export default Card;
