import image from '../image.webp';
import'../Style/card.css';
const Card = () => {
  // Generate random image URL
  const getRandomImage = () => {
    // Replace this logic with your own random image URL generation
    const randomImageUrls = [image, image, image];
    const randomIndex = Math.floor(Math.random() * randomImageUrls.length);
    return randomImageUrls[randomIndex];
  };

  // Array of card data
  const cardData = [
    { name: 'One Plus', message: 'Android Device' },
    { name: 'Samsung S22', message: 'Android Device' },
    { name: 'Iphone 14', message: 'IOS Device' },
  ];

  return (
    <>
      {cardData.map((item, index) => (
     <div class="scene">
     <div class="card">
       <div class="card__face card__face--front">
         <img src="https://i.loli.net/2019/11/23/cnKl1Ykd5rZCVwm.jpg" />
       </div>
       <div class="card__face card__face--back">
         <img src="https://i.loli.net/2019/11/16/cqyJiYlRwnTeHmj.jpg" />
       </div>
     </div>
     <div class="card">
       <div class="card__face card__face--front">
         <img src="https://i.loli.net/2019/11/16/FLnzi5Kq4tkRZSm.jpg" />
       </div>
       <div class="card__face card__face--back">
         <img src="https://i.loli.net/2019/10/18/buDT4YS6zUMfHst.jpg" />
       </div>
     </div>
     <div class="card">
       <div class="card__face card__face--front">
         <img src="https://i.loli.net/2019/10/18/uXF1Kx7lzELB6wf.jpg" />
       </div>
       <div class="card__face card__face--back">
         <img src="https://i.loli.net/2019/11/03/RtVq2wxQYySDb8L.jpg" />
       </div>
     </div>
   </div>
      ))}
  </>
  );
};

export default Card;
