import React from "react";
import image from './image.webp';
import "./Style/App.css";

import Card from "./components/Card";
import Navigation from "./components/Navigation";

function App() {
  // Array of card data
  const cardData = [
    { name: 'One Plus', message: 'Android Device', img: image },
    { name: 'Samsung S22', message: 'Android Device', img: image },
    { name: 'Iphone 14', message: 'iOS Device', img: image },
  ];

  return (
    <div className="containers">
      <div className="text-container">
        <h1>NovaTech</h1>
      </div>
      <Navigation />
      {cardData.map((item, index) => (
        <figure class="snip1577" key={index}>
          <img src={item.img} alt="sample99" />
          <figcaption>
            <h3>{item.name}</h3>
            <h4>{item.message}</h4>

          </figcaption>
          <a href="#"></a>
        </figure>
      ))}
    </div>
  );
}

export default App;
