import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../Style/App.css';

import cardData from './cardData'; // Assuming cardData is exported from a separate file

function Card() {
  const navigate = useNavigate();

  const handleClick = (item) => {
    navigate('/blogs', {
      state: {
        deviceName: item.name,
        description: item.description,
        image: item.img,
        storage1: item.Storage1,
        storage2: item.Storage2,
        storage3: item.Storage3
      }
    });
  };

  return (
    <>
      {cardData.map((item, index) => (
        <div className="card" key={index}>
          <figure className="card__thumb">
            <img src={item.img} alt={`Picture of ${item.name}`} className="card__image" />
            <figcaption className="card__caption">
              <h2 className="card__title">{item.name}</h2>
              <p className="card__snippet">{item.description}</p>
              <a href="/blogs" className="card__button" onClick={() => handleClick(item)}>
                {item.message}
              </a>
            </figcaption>
          </figure>
        </div>
      ))}
      <p className="disclaimer">
        All pictures were taken from{' '}
        <a className="disclaimer__link" href="https://unsplash.com/" target="_blank" rel="noopener noreferrer">
          Unsplash.
        </a>
      </p>
    </>
  );
}
export default Card;
