import'../Style/App.css';
import { useLocation } from 'react-router-dom';

function Discription() {
    let location = useLocation();
    const name = location.state.deviceName;
    const Ddiscription = location.state.Ddiscription;
    const Image = location.state.img;
    const storage1 = location.state.Storage1;
    const storage2 = location.state.Storage2;
    const storage3 = location.state.Storage3;

    return(  
		<>
		    <main class="discription-container">

        <div class="left-column">
            <img data-image="black" class="active" src={Image} alt=""/>
        </div>

        <div class="right-column">

            <div class="product-description">
                <span>SmartPhones</span>
                <h1>{name} </h1>
                <h3>{Ddiscription}</h3>
            </div>

            <div class="product-configuration">

               

                <div class="cable-config">
                    <span>storage space</span>

                    <div class="cable-choose">
                        <button>{storage1}</button>
                        <button>{storage2}</button>
                        <button>{storage3}</button>
                    </div>

                </div>
            </div>

            <div class="product-price">
                <span>Â£600</span>
                <a href="#" class="cart-btn">Add to cart</a>
            </div>
        </div>
		</main>
		</>
)
}
export default Discription;
