import React from "react";
import image from './image.webp';
import "./Style/App.css";

import Card from "./components/Card";
import Navigation from "./components/Navigation";
function App() {
  const getRandomImage = () => {
    // Replace this logic with your own random image URL generation
    const randomImageUrls = [image, image, image];
    const randomIndex = Math.floor(Math.random() * randomImageUrls.length);
    return randomImageUrls[randomIndex];
  };

  // Array of card data
  const cardData = [
    { name: 'One Plus', message: 'Android Device' },
    { name: 'Samsung S22', message: 'Android Device' },
    { name: 'Iphone 14', message: 'IOS Device' },
  ];

  return (
    <div className="containers">
      <div className="text-container">
<h1>NovaTech</h1>

     </div>

    <Navigation/>

      {cardData.map((item, index) => (

   <figure class="snip1577">
   <img src={getRandomImage} alt="sample99" />
   <figcaption>
     <h3>{cardData.name}</h3>
     <h4>{cardData.message}</h4>
   </figcaption>
   <a href="#"></a>
 </figure>

    ))}
    </div>
  );
}

export default App;
